import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Container from '@material-ui/core/Container';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import { withTranslation } from '~/i18n';
import logo from '../../public/images/profile-logo.svg';
import brand from '~/public/text/brand';
import { useText } from '~/theme/common';
import useStyles from './wp-style';
import ContactForm from '../Contact/Form';
import nwlogo1 from '../../public/images/profile/wp-images/nv-logo1.jpg';
import tf2 from '../../public/images/profile/ux-images/tafs2.jpg';
import users from '../../public/images/profile/tf-images/users.jpg';
import tfmock from '../../public/images/profile/tf-images/tapandfork.jpg';

const NVmartial = () => {
  const [pageNumber, setPageNumber] = useState(1);
  const classes = useStyles();
  const text = useText();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));


    return (
      <div className={classes.root}>
        <Container maxWidth="md" component="footer">

          <Typography variant="h6" align="left" >
            Project Description:
          </Typography>

          <Typography className={text.paragraph}>
          New school of martial arts was opening in Dallas, GA. 
          </Typography>

          <img src={nwlogo1} align="center" width="500px" alt="AWS accounts map" />
          <Typography className={text.paragraph}>
            <b>My role in this project</b> - I was hired to create a branding, website and give some essential graphics support.
          </Typography>
          <Typography variant="h6" align="left" color="primary">
          Desk research:
          </Typography>
          <Typography className={text.paragraph}>
          I made a detailed analysis of existing martial arts schools in Dalas and Acworth, GA. Most successful businesses were using industry specialized marketing companies. The targeted auditory was parents looking for kids classes and adult weight loss programs. 
          </Typography>
          <Typography className={text.paragraph}>
          As part of my desk research I analyzed the most popular search words that people put in google, while searching for martial art classes.  I advised the owner to choose a name for the business considering available domains with the best search words. His idea was to organize the school in an untraditional way, so he chose New Vision Martial Arts. We could purchase a great domain : nv-martialarts.com. It gave him a lot of advantages later on.
          </Typography>
          <Typography variant="h6" align="left" color="primary">
          User research:
          </Typography>
          <Typography className={text.paragraph}>
          TI asked the opinions of 4 teenagers, 3 kids, 3 mothers and 2 fathers about the name of the business and chosen graphics styles. It helped to keep focus on different perspectives of different users.<br/><br/>
          I had this list of customer goals:
          <ul>
                <li>Look up good classes in their area</li>
                <li>See if they like the teachers and atmosphere of the club</li>
                <li>Find a good time in their schedule</li>
                <li>Find out the price and compare programs</li>
                <li>Signup for a free class</li>
                <li>Ask questions</li>
                <li>Pay online</li>
                <li>Signup for a program</li>
              </ul>

          </Typography>

        </Container>      
    </div>
    );
  }

  NVmartial.propTypes = {
    t: PropTypes.func.isRequired
  };

  export default withTranslation(['profile-landing'])(NVmartial);
