import React from 'react';
import PropTypes from 'prop-types';
import Container from '@material-ui/core/Container';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import { withTranslation } from '~/i18n';
import logo from '~/public/images/profile-logo.svg';
import brand from '~/public/text/brand';
import { useText } from '~/theme/common';
import useStyles from './tf-style';
import ContactForm from '../Contact/Form';

const TapFork = () => {
  const classes = useStyles();
  const text = useText();


  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

    return (
      <div className={classes.root}>
      <Container maxWidth="lg" component="footer">
        <Grid container spacing={6} direction={isMobile ? 'column' : 'row'}>
          <Grid item xs={12} md={5}>
            <div>
            <Typography className={text.caption}>Problem:<br /></Typography>
            <Typography className={text.paragraph}>
            Shortcomings of the existing applications to make the pricing strategy more attractive to restaurant owners and to allow them to fully control their marketing campaigns.
            </Typography>
              <Typography className={text.paragraph}>

              Tap & Fork is an application that allows people to find restaurants. Conceptually it is similar to applications like Yelp, OpenTable or Zomato, Tap & Fork is built to address some of the shortcomings of the existing applications to make the pricing strategy more attractive to restaurant owners and to allow them to fully control their marketing campaigns. With Tap & Fork, restaurant owners can easily upload images, create coupons, and run promotions.
                <br />
              </Typography>

              <Divider className={classes.divider} />

              <Typography variant="h4" align="left">
                My role in this project
              </Typography>
              <Typography className={text.paragraph}>
                <ul>
                  <li>UX and Graphic design of the application</li>
                  <li>UX designe for online ad maker</li>
                  <li>Graphic support for the project</li>
                </ul>
                <br />
              </Typography>
              <Typography variant="h5" align="left">
                UX and Graphic design of the application
              </Typography>
              <Typography className={text.paragraph}>
                <ul>
                  <li>UX and Graphic design of the application</li>
                  <li>UX designe for online ad maker</li>
                  <li>Graphic support for the project</li>
                </ul>
                <br />
              </Typography>


            </div>
          </Grid>
          <Grid item xs={12} md={7}>

            <ContactForm />
          </Grid>
        </Grid>
      </Container>
    </div>
    );
  }

  TapFork.propTypes = {
    t: PropTypes.func.isRequired
  };

  export default withTranslation(['profile-landing'])(TapFork);
